import React, { useState } from 'react';
import { CaseStudy } from '../types';

interface CaseStudiesProps {
  studies: CaseStudy[];
}

const CaseStudies: React.FC<CaseStudiesProps> = ({ studies }) => {
  const [current, setCurrent] = useState(0);

  const next = () => setCurrent((current + 1) % studies.length);
  const prev = () => setCurrent((current - 1 + studies.length) % studies.length);

  return (
    <section className="case-studies">
      <button onClick={prev}>Previous</button>
      <div className="slider">
        {studies.map((study, index) => (
          <div key={index} className={`slide ${index === current ? 'active' : ''}`}>
            <h2>{study.title}</h2>
            <p>{study.description}</p>
          </div>
        ))}
      </div>
      <button onClick={next}>Next</button>
    </section>
  );
};

export default CaseStudies;
