import React, { useEffect, useState } from 'react';
import axios from 'axios';
import Filters from './Filters';
import { Brand } from '../types';
import SectionTitle from './sectionTitle';
import { Heading } from 'react-bulma-components';

const Brands: React.FC = () => {
  const [brands, setBrands] = useState<Brand[]>([]);
  const [filteredBrands, setFilteredBrands] = useState<Brand[]>([]);
  const [filter, setFilter] = useState('');
  const [sortOption, setSortOption] = useState('');

  useEffect(() => {
    const fetchBrands = async () => {
      const response = await axios.get<Brand[]>('/api/brands'); // Replace with actual API endpoint
      setBrands(response.data);
      setFilteredBrands(response.data);
    };
    fetchBrands();
  }, []);

  const handleFilter = (filter: string) => {
    setFilter(filter);
    const filtered = brands.filter((brand: Brand) =>
      brand.name.toLowerCase().includes(filter.toLowerCase())
    );
    setFilteredBrands(filtered);
  };

  const handleSort = (sortOption: string) => {
    setSortOption(sortOption);
    let sorted = [...filteredBrands];
    if (sortOption === 'name-asc') {
      sorted.sort((a, b) => a.name.localeCompare(b.name));
    } else if (sortOption === 'name-desc') {
      sorted.sort((a, b) => b.name.localeCompare(a.name));
    }
    setFilteredBrands(sorted);
  };

  return (
    <section className="brands">
      <Filters onSort={handleSort} onFilter={handleFilter} />
      <div className="brands-grid">
        {filteredBrands.map((brand) => (
          <div key={brand.id} className="brand">
            <img src={`/images/brands/${brand.logo}`} alt={brand.name} />
          </div>
        ))}
      </div>

    <div>
    <Container className='m-6'>
        <SectionTitle title='Youâ€™ll be in good company' />
        <Columns mt={6}>
            {
              cases.map((service: DisplayDTO)=>(
                  <Columns.Column size={'one-third'} pt={1}>
                      <Image src={`/images/stock/${service.image}`} size={''} className='image is-square'/>
                      <Container>
                        <img src='/images/assets/rectangle.svg' alt={`case icon`}/>
                        <Heading className='has-text-black mt-2 mb-2' size={5} weight='semibold'>
                            {service.title}
                        </Heading>
                        <Heading className='has-text-black' subtitle weight='light' size={6}>
                            {service.description}
                        </Heading>
                      </Container>  
                  </Columns.Column>
              ))
            }
        </Columns>
    </Container>
</div>
</section>

  );
};

export default Brands;
